custom_scss:
@import url('https://fonts.googleapis.com/css2?family=Encode+Sans+Condensed:wght@100;200;300;400;500;600;700;800;900&display=swap');
#page-login {
    background-image: linear-gradient(to right,
            rgba(15, 15, 44, 0.825) 0%,
            rgba(217, 217, 217, 0.25) 50%,
            rgba(15, 15, 44, 0.825) 100%),
        url('/assets/iau/images/company_background.png');
    background-repeat: no-repeat !important;
    background-size: cover !important;
    background-position: center !important;
    background-color: rgb(30, 30, 53) !important;
    height : 100vh !important; 
}
#page-login .page-content-wrapper {
    display : flex !important;
    justify-content : center !important;
    align-items : center !important;
}
.for-signup .page-card-head,
.for-forgot .page-card-head {
    display : none !important;
}
.for-signup .page-card,
.for-forgot .page-card {
    background-image: linear-gradient(#ffffff 25%, #efe6d2 75%) !important;
}
.navbar {
    height : 90px !important;
}
.nav-item a {
    font-family : 'Encode Sans Condensed', system-ui !important;
    font-weight : 500 !important;
    font-size : 16px !important;
}

js:
document.addEventListener("DOMContentLoaded", () => {
    const navbarUl = document.querySelectorAll(".nav-item");

    navbarUl.forEach(navItem => {
        const loginLink = navItem.querySelector('a.btn-login-area');
    
        if (loginLink && loginLink.innerText.trim() === "Login") {
            loginLink.innerText = "E SERVICES";
            loginLink.href = "/Login";
        }
    });
    const signUpLink = document.querySelector('.form-signup .sign-up-message a');
    if (signUpLink) {
        signUpLink.href = '/Login';
    }
    const signUpLinkForgot = document.querySelector('.form-forgot .sign-up-message a');
    if (signUpLinkForgot) {
        signUpLinkForgot.href = '/Login';
    }
});

