  background-color: #101423 !important;
  color: #fff !important;
  border-radius: 0 !important;
}
.for-login .social-logins .login-divider {
  font-family: DM Sans, sans-serif;
  font-size: 20px;
  color: #929292;
  font-weight: 700;
}
.for-login .social-logins .login-button-wrapper {
  padding: 0 !important;
}
.for-login .social-logins .login-button-wrapper .btn-login-with-email-link {
  padding: 15px 0px !important;
  font-size: 22px !important;
  font-weight: 700 !important;
  background-color: #fffbf1 !important;
  border: 1px solid !important;
  border-image-slice: 1 !important;
  border-image: linear-gradient(to bottom, #324362 100%, #324362 0%) !important;
  color: #101423 !important;
  cursor: pointer !important;
  border-radius: 0 !important;
}
.for-login .sign-up-message,
.for-login .sign-up-message a,
.for-forgot .sign-up-message a {
  font-size: 22px !important;
  font-weight: 700 !important;
  color: #101423 !important;
}

