// Function to create and insert a custom header div
function createHeaderDiv(headerText, targetDiv, formClass) {
  // Create the new div element
  const newDiv = document.createElement('div');
  newDiv.style.width = '598px';
  newDiv.style.height = '67px';
  newDiv.style.marginBottom = '33px';
  newDiv.style.display = 'flex';
  newDiv.style.justifyContent = 'space-between';
  newDiv.style.alignItems = 'center';

  // Create the h2 element with custom text
  const h2Element = document.createElement('h2');
  h2Element.style.fontFamily = "'Encode Sans Condensed', sans-serif";
  h2Element.style.fontSize = '36px';
  h2Element.style.lineHeight = '45px';
  h2Element.style.color = '#3D4667';
  h2Element.style.fontWeight = '700';
  h2Element.style.setProperty('margin-top', '0px', 'important');
  h2Element.textContent = headerText;

  // Create the img element
  const imgElement = document.createElement('img');
  imgElement.src = '/assets/iau/images/Auth Logo.png';
  imgElement.alt = 'company_logo';

  // Append the h2 and img elements to the new div
  newDiv.appendChild(h2Element);
  newDiv.appendChild(imgElement);

  // Insert the new div before the form in the target div
  const formElement = targetDiv.querySelector(formClass);
  targetDiv.insertBefore(newDiv, formElement);
}

// Select the login-content div for both login and forgot password sections
const loginContentDiv = document.querySelector('.for-login .login-content');
const forgotPasswordContentDiv = document.querySelector(
  '.for-forgot .login-content'
);

// Create and insert header for login content
createHeaderDiv('Sign In', loginContentDiv, '.form-login');

// Create and insert header for forgot password content
createHeaderDiv('Forgot Password', forgotPasswordContentDiv, '.form-forgot');

// Select the elements
const loginContent = document.querySelector('.for-login .login-content');
const forLoginDiv = document.querySelector('.for-login .sign-up-message');
const forLoginLink = document.querySelector('.for-login .sign-up-message a');
const forForgotPasswordLink = document.querySelector(
  '.for-login .forgot-password-message a'
);
const forSignupLink = document.querySelector('.for-signup .sign-up-message a');
const forForgotLink = document.querySelector('.for-forgot .sign-up-message a');

// Add event listener to the for-login sign-up message link
forLoginLink.addEventListener('click', function (event) {
  forLoginDiv.style.display = 'none';
  loginContent.style.display = 'none';
});

// Add event listener to the for-signup sign-up message link
forSignupLink.addEventListener('click', function (event) {
  forLoginDiv.style.display = 'block';
  loginContent.style.display = 'block';
});

// Add event listener to the for-forgot sign-up message link
forForgotLink.addEventListener('click', function (event) {
  forLoginDiv.style.display = 'block';
  loginContent.style.display = 'block';
});

// Add event listener to the for-forgot password link
forForgotPasswordLink.addEventListener('click', function (event) {
  forLoginDiv.style.display = 'none';
  loginContent.style.display = 'none';
});

